def City(response):
    return {
        "city": response.city.name,
        "continent_code": response.continent.code,
        "continent_name": response.continent.name,
        "country_code": response.country.iso_code,
        "country_name": response.country.name,
        "dma_code": response.precise_location.metro_code,
        "is_in_european_union": response.country.is_in_european_union,
        "latitude": response.precise_location.latitude,
        "longitude": response.precise_location.longitude,
        "postal_code": response.postal.code,
        "region": response.subdivisions[0].iso_code if response.subdivisions else None,
        "time_zone": response.precise_location.time_zone,
    }


def Country(response):
    return {
        "country_code": response.country.iso_code,
        "country_name": response.country.name,
    }
